INSERT INTO vscp_type (type,link_to_class,token,description) VALUES (40,542,'VSCP_TYPE_CONTROL_ALL_LAMPS_ON','Turn on all lamps in a zone.\n\n | Data byte | Description                                                        | \n | :---------: | -----------                                                        | \n | 0         | Not used.                                                          | \n | 1         | Zone for which event applies to (0-255). 255 is all zones.         | \n | 2         | Sub-zone for which event applies to (0-255). 255 is all sub-zones. | \n\nSee also [CLASS1.CONTROL, Type=2](./class1.control.md#type2)\n');
INSERT INTO vscp_type (type,link_to_class,token,description) VALUES (41,542,'VSCP_TYPE_CONTROL_ALL_LAMPS_OFF','Turn off all lamps in a zone.\n\n | Data byte | Description                                                        | \n | :---------: | -----------                                                        | \n | 0         | Not used.                                                          | \n | 1         | Zone for which event applies to (0-255). 255 is all zones.         | \n | 2         | Sub-zone for which event applies to (0-255). 255 is all sub-zones. | \n\nSee also [CLASS1.CONTROL, Type=2](./class1.control.md#type2)\n');
INSERT INTO vscp_type (type,link_to_class,token,description) VALUES (42,542,'VSCP_TYPE_CONTROL_LOCK','Lock devices in a zone.\n\n | Data byte | Description                                                        | \n | :---------: | -----------                                                        | \n | 0         | Not used.                                                          | \n | 1         | Zone for which event applies to (0-255). 255 is all zones.         | \n | 2         | Sub-zone for which event applies to (0-255). 255 is all sub-zones. | \n\n');
