INSERT INTO vscp_type (type,link_to_class,token,description) VALUES (25,65535,'CLASS2.VSCPD','A timer has been started.\n\nArgument is timer ID and start time for the timer. \n\n | Byte | Description                                        | \n | :----: | -----------                                        | \n | 0    | 32 bit timer ID. MSB.                              | \n | 1    | 32 bit timer ID.                                   | \n | 2    | 32 bit timer ID.                                   | \n | 3    | 32 bit timer ID. LSB.                              | \n | 4    | Start time in milliseconds as a 32-bit value. MSB. | \n | 5    | Start time in milliseconds as a 32-bit value.      | \n | 6    | Start time in milliseconds as a 32-bit value.      | \n | 7    | Start time in milliseconds as a 32-bit value. LSB. | \n\nMax timer value is about 49 days. ');
INSERT INTO vscp_type (type,link_to_class,token,description) VALUES (26,65535,'CLASS2.VSCPD','A timer has been paused.\n\nArgument is timer ID and time when timer was paused. \n\n | Byte | Description                                        | \n | :----: | -----------                                        | \n | 0    | 32 bit timer ID. MSB.                              | \n | 1    | 32 bit timer ID.                                   | \n | 2    | 32 bit timer ID.                                   | \n | 3    | 32 bit timer ID. LSB.                              | \n | 4    | Start time in milliseconds as a 32-bit value. MSB. | \n | 5    | Start time in milliseconds as a 32-bit value.      | \n | 6    | Start time in milliseconds as a 32-bit value.      | \n | 7    | Start time in milliseconds as a 32-bit value. LSB  | \n');
INSERT INTO vscp_type (type,link_to_class,token,description) VALUES (27,65535,'CLASS2.VSCPD','A timer has been restarted.\n\nArgument is timer ID and time when timer was restarted. \n\n | Byte | Description                                        | \n | :----: | -----------                                        | \n | 0    | 32 bit timer ID. MSB.                              | \n | 1    | 32 bit timer ID.                                   | \n | 2    | 32 bit timer ID.                                   | \n | 3    | 32 bit timer ID. LSB.                              | \n | 4    | Start time in milliseconds as a 32-bit value. MSB. | \n | 5    | Start time in milliseconds as a 32-bit value.      | \n | 6    | Start time in milliseconds as a 32-bit value.      | \n | 7    | Start time in milliseconds as a 32-bit value. LSB. | \n');
